function get-screenshot {

start-sleep -seconds 1

if ((test-path 'c:\Program Files\Greenshot\greenshot.exe') -or (test-path 'C:\Program Files\ShareX\ShareX.exe')){
[Windows.Forms.Sendkeys]::SendWait('{PrtSc}')}

else {Start-Process $env:windir\system32\SnippingTool.exe}

