#region +++++++++++++++++++++ Gather Advanced Info +++++++++++++++++++++ 

Function advancedMode {

Add-Type -AssemblyName System.Windows.Forms
#logging
Add-Content -Path $logDir -Value "$date - $Env:Username - $Env:ComputerName - Ran Show-SystemInfo.ps1 v$ver - Advanced" -Force -ErrorAction SilentlyContinue
  
  #Required Functions

  function Get-Uptime {
    $os = Get-WmiObject win32_operatingsystem
    $uptime = (Get-Date) - ($os.ConvertToDateTime($os.lastbootuptime))
    $Display = 'Uptime: ' + $Uptime.Days + ' days, ' + $Uptime.Hours + ' hours, ' + $Uptime.Minutes + ' minutes' 
    Write-Output $Display
  }

  function sum {
    begin { $x = 0 }
    process { $x += $_ }
    end { $x }
  }

  #Gather Advanced Data
  
  $netinfo = Get-WmiObject Win32_NetworkAdapterConfiguration | Where-Object { $_.IPAddress } | Select-Object IPAddress,MacAddress,Description,DNSServerSearchOrder,DHCPServer,DefaultIPGateway
  $ip0 = $netinfo | Select-Object -Expand IPAddress -First 1 | Where-Object { $_ -like '*.*.*.*' } | Out-String
  $ip0 = $ip0.trim()
  $mac0 = $netinfo| Select-Object -Expand MacAddress -First 1 | Out-String
  $nicname0 = $netinfo | Select-Object -Expand Description -First 1 | Out-String
  $ip1 = $netinfo[1] | Select-Object -Expand IPAddress | Where-Object { $_ -like '*.*.*.*' } | Out-String
  $ip1 = $ip1.trim()
  $mac1 = $netinfo[1] | Select-Object -Expand MacAddress | Out-String
  $nicname1 = $netinfo[1] | Select-Object -Expand Description | Out-String
  $domain = $env:USERDOMAIN
  $uptime = get-uptime
  $WinVersion = (Get-WmiObject -class Win32_OperatingSystem).Caption
  $compresults = Get-WmiObject -class Win32_ComputerSystem
  $compManufacturer = $compresults.Manufacturer
  $compmodel = $compresults.Model
  $compserial = (Get-WmiObject -class win32_bios).SerialNumber
  $procname = (Get-WmiObject -class Win32_Processor).name
  $memamount = (Get-WmiObject -class 'cim_physicalmemory' | Measure-Object -Property Capacity -Sum).Sum
  $memamount = $memamount / 1GB
  $pubip = (New-Object Net.WebClient).DownloadString('http://ipinfo.io/ip').trim()
  $dnsservers = $netinfo | Select-Object -ExpandProperty DNSServerSearchOrder
  $dhcpserver = $netinfo | Select-Object -ExpandProperty DHCPServer
  $defgate = $netinfo | Select-Object -ExpandProperty DefaultIPGateway | Where-Object { $_ -like '*.*.*.*' } | Out-String
  $defgate = $defgate.Trim()

  #Display Form

  if (!$ip1) {
    $result = [Windows.Forms.MessageBox]::Show("$helpdeskname CONTACT INFO:`nPhone: $helpdesknumber`nEmail: $helpdeskemail`n`nComputer Name: $compname`nUser Name: $username`nTeamviewer ID: $tvid`n`nNetwork Adapter 1:`n $nicname0`nIP Address: $ip0`nMAC Address: $mac0`nDNS Servers: $dnsservers`nDHCP Server: $dhcpserver`n`nPublic IP: $pubip`nDomain: $domain`nComputer Manufacturer: $compManufacturer`nComputer Model: $compmodel`nComputer Serial: $compserial`nProcessor: $procname`nMemory Amount: $memamount GB`nSystem Uptime: $uptime`nWindows Version: $winversion`n`nCopy this information to your clipboard?",$formtitle,'YesNoCancel','Information')
  }
  else {
    $result = [Windows.Forms.MessageBox]::Show("$helpdeskname CONTACT INFO:`nPhone: $helpdesknumber`nEmail: $helpdeskemail`n`nComputer Name: $compname`nUser Name: $username`nTeamviewer ID: $tvid`n`nNetwork Adapter:`n $nicname0`nIP Address: $ip0`nMAC Address: $mac0`nDNS Servers: $dnsservers`nDHCP Server: $dhcpserver`n`nNetwork Adapter 2:`n$nicname1`nIP Address: $ip1`nMAC Address: $mac1`nPublic IP: $pubip`nDomain: $domain`nComputer Manufacturer: $compManufacturer`nComputer Model: $compmodel`nComputer Serial: $compserial`nProcessor: $procname`nMemory Amount: $memamount GB`nSystem Uptime: $uptime`nWindows Version: $winversion`n`nCopy this information to your clipboard?",$formtitle,'YesNoCancel','Information')
  }
  
  #If yes, copy to clipboard

  if ($result -eq 'Yes'){
    #adv copy to clipboard logging
    Add-Content -Path $logDir -Value "$date - $Env:Username - $Env:ComputerName - Ran Show-SystemInfo.ps1 v$ver - Advanced Mode - Copied" -Force -ErrorAction SilentlyContinue
    
    Write-Output "Computer Name: $compname`nUser Name: $username`neamviewer ID: $tvid`n`nNetwork Adapter 1: $nicname0`nIP Address: $ip0`nMAC Address: $mac0`nNetwork Adapter 2: $nicname1`nIP Address: $ip1`nMAC Address: $mac1`nDNS Servers: $dnsservers`nDHCP Server: $dhcpserver`nPublic IP: $pubip`nDomain: $domain`nComputer Manufacturer: $compManufacturer`nComputer Model: $compmodel`nComputer Serial: $compserial`nProcessor: $procname`nMemory Amount: $memamount GB`nSystem Uptime: $uptime`nVersion: $winversion`n" | & "$env:windir\system32\clip.exe"
    [Windows.Forms.MessageBox]::Show('Copied',$formtitle,'Ok','Information')
  }

  if ($result -eq 'no'){return}

}

#endregion advanced mode gather

#region +++++++++++++++++++++ Network Diagnostics +++++++++++++++++++++

Function test-network
{
	
	$tempfile = "$env:TEMP\netdiag.txt"
	$date = (Get-Date).ToString()
	
	#region Begin GUI{
	
	$Form = New-Object system.Windows.Forms.Form
	$Form.ClientSize = '720,430'
	$Form.text = $NetworkTestFormTitle
	$Form.BackColor = '#ffffff'
	$Form.TopMost = $true
