	$ConsoleTextBox = New-Object system.Windows.Forms.TextBox
	$ConsoleTextBox.ReadOnly = $true
	$ConsoleTextBox.multiline = $true
	$ConsoleTextBox.width = 700
	$ConsoleTextBox.height = 350
	$ConsoleTextBox.location = New-Object System.Drawing.Point(10, 10)
	$ConsoleTextBox.Font = 'Consolas,12'
	$ConsoleTextBox.TextAlign = 'Left'
	$ConsoleTextBox.ScrollBars = 'Vertical'
	
	$quicktestbutton = New-Object system.Windows.Forms.Button
	$quicktestbutton.text = 'Quick Test'
	$quicktestbutton.width = 75
	$quicktestbutton.height = 54
	$quicktestbutton.location = New-Object System.Drawing.Point(10, 365)
	$quicktestbutton.Font = 'Consolas'
	$quicktestbutton.Add_Click({ get-nettest })
	
	$wifitestbutton = New-Object system.Windows.Forms.Button
	$wifitestbutton.text = 'WiFi Test'
	$wifitestbutton.width = 75
	$wifitestbutton.height = 54
	$wifitestbutton.location = New-Object System.Drawing.Point(180, 365)
	$wifitestbutton.Font = 'Consolas'
	$wifitestbutton.Add_Click({ get-wifitest })
	
	$networkfixbutton = New-Object system.Windows.Forms.Button
	$networkfixbutton.text = 'Attempt Fix'
	$networkfixbutton.width = 75
	$networkfixbutton.height = 54
	$networkfixbutton.location = New-Object System.Drawing.Point(305, 365)
	$networkfixbutton.Font = 'Consolas'
	$networkfixbutton.Add_Click({ Repair-NetworkTrouble })
	
	$ClearTextButton = New-Object system.Windows.Forms.Button
	$ClearTextButton.text = 'Clear Text'
	$ClearTextButton.width = 75
	$ClearTextButton.height = 54
	$ClearTextButton.location = New-Object System.Drawing.Point(425, 365)
	$ClearTextButton.Font = 'Consolas'
	$ClearTextButton.Add_Click({ Clear-Text })
	
	$CopyStuffbutton = New-Object system.Windows.Forms.Button
	$CopyStuffbutton.text = 'Copy to Clipboard'
	$CopyStuffbutton.width = 75
	$CopyStuffbutton.height = 54
	$CopyStuffbutton.location = New-Object System.Drawing.Point(510, 365)
	$CopyStuffbutton.Font = 'Consolas'
	$CopyStuffbutton.Add_Click({ copyStuff })
	
	$Closebutton = New-Object system.Windows.Forms.Button
	$Closebutton.text = 'Close'
	$Closebutton.width = 75
	$Closebutton.height = 54
	$Closebutton.location = New-Object System.Drawing.Point(630, 365)
	$Closebutton.Font = 'Consolas'
	$Closebutton.Add_Click({ $Form.close() })
	
	$wantestbutton = New-Object system.Windows.Forms.Button
	$wantestbutton.text = 'Internet Speed'
	$wantestbutton.width = 75
	$wantestbutton.height = 54
	$wantestbutton.location = New-Object System.Drawing.Point(95, 365)
	$wantestbutton.Font = 'Consolas'
	$wantestbutton.Add_Click({ test-wanspeed })
	
	$Form.controls.AddRange(@($ConsoleTextBox, $quicktestbutton, $wantestbutton, $CopyStuffbutton, $Closebutton, $wifitestbutton, $networkfixbutton, $ClearTextButton))
	
	Clear-Content -Path $tempfile -Force
	
	$ConsoleTextBox.Text = (Get-Content -Path $tempfile) -join [Environment]::NewLine
	
	#endregion
	
	#region Network Diagnostic Functions
	
	Function get-nettest
	{
		
		$date = (Get-Date).ToString()
		#logging
		Add-Content -Path $logDir -Value "$date - $Env:Username - $Env:ComputerName - Ran Show-SystemInfo.ps1 v$ver - Quick Network Test" -Force -ErrorAction SilentlyContinue
		
		Add-Content -Path $tempfile -Force -Value "~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~"
		
		$ConsoleTextBox.Text = (Get-Content -Path $tempfile) -join [Environment]::NewLine
		
		#region Ping Stuff
		
		#localhost
		
		Add-Content -Path $tempfile -Force -Value "$date - Network Diagnostics $env:COMPUTERNAME - $env:USERNAME"
		
		if (Test-Connection $env:COMPUTERNAME -Quiet -Count 1 -ErrorAction SilentlyContinue)
		{
			Write-Output "SUCCESS: Pinging localhost: $env:COMPUTERNAME " |
			Add-Content -Path $tempfile
		}
		if (!(Test-Connection $env:COMPUTERNAME -Quiet -Count 1 -ErrorAction SilentlyContinue))
		{
			Write-Output "FAILED: Pinging localhost: $env:COMPUTERNAME" |
			Add-Content -Path $tempfile
		}
		
		#defaultgateway
		
		$defgate = Get-WmiObject -Class Win32_IP4RouteTable | Where-Object { $_.destination -eq '0.0.0.0' -and $_.mask -eq '0.0.0.0' } | Sort-Object metric1 | Select-Object nexthop, metric1, interfaceindex -ExpandProperty nexthop
		if (!$defgate)
		{
			Write-Output 'FAILED: No Default Gateway' |
			Add-Content -Path $tempfile
		}
		if ($defgate)
		{
			if (Test-Connection $defgate -Quiet -Count 1 -ErrorAction SilentlyContinue)
			{
				Write-Output "SUCCESS: Pinging Default Gateway: $defgate" |
				Add-Content -Path $tempfile
			}
			if (!(Test-Connection $defgate -Quiet -Count 1 -ErrorAction SilentlyContinue))
			{
				Write-Output "FAILED: Pinging Default Gateway: $defgate" |
				Add-Content -Path $tempfile
			}
		}
		
		#dnsservers
		
		$NetAdapter = Get-WMIObject -Class 'Win32_NetworkAdapterConfiguration' -Filter ('IPEnabled=TRUE')
		$dnsservers = $NetAdapter | Select-Object DNSServerSearchOrder -ExpandProperty DNSServerSearchOrder
		
		foreach ($dnsserver in $dnsservers)
		{
			if (Test-Connection $dnsserver -Quiet -Count 1 -ErrorAction SilentlyContinue)
			{
				Write-Output "SUCCESS: Pinging DNS Server: $dnsserver" |
				Add-Content -Path $tempfile
			}
			if (!(Test-Connection $dnsserver -Quiet -Count 1 -ErrorAction SilentlyContinue))
			{
				Write-Output "FAILED: Pinging DNS Server: $dnsserver" |
				Add-Content -Path $tempfile
			}
		}
		
		#internet
		
		$internetaddresses = '8.8.8.8', '4.2.2.2'
		
		foreach ($internetaddress in $internetaddresses)
		{
			if (Test-Connection $internetaddress -Quiet -Count 1 -ErrorAction SilentlyContinue)
			{
				Write-Output "SUCCESS: Pinging Internet Address: $internetaddress" |
				Add-Content -Path $tempfile
			}
			if (!(Test-Connection $internetaddress -Quiet -Count 1 -ErrorAction SilentlyContinue))
			{
				Write-Output "FAILED: Pinging Internet Address: $internetaddress" |
				Add-Content -Path $tempfile
			}
		}
		
		
		#endregion
		
		#region Test DNS resolution
		
		$dnsnames = 'microsoft.com', 'google.com', 'yahoo.com'
		
		foreach ($dnsname in $dnsnames)
		{
			
			Try
			{
				$dnschk = [Net.DNS]::GetHostByName($dnsname)
				$dnsresolvedip = $dnschk | Select-Object -ExpandProperty AddressList
				Write-Output "SUCCESS: DNS Lookup of $dnsname resolves to $dnsresolvedip" |
				Add-Content -Path $tempfile
			}
			
			Catch
			{
				Write-Output "FAILED: Cannot resolve $dnsname" |
				Add-Content -Path $tempfile
			}
		}
		#endregion
		
		
		
