		$ConsoleTextBox.Text = (Get-Content -Path $tempfile) -join [Environment]::NewLine
		Start-Sleep -Seconds 1
		
		try
		{
			$Request = Get-Date; $tf = New-Object net.webclient
			$a = Get-Date; $tf.Downloadfile('http://client.akamai.com/install/test-objects/10MB.bin', "$env:TEMP/10MB.bin"); $speed = "$((10/((Get-Date) - $a).TotalSeconds) * 8)"
			$speed = [math]::Round($speed, 2)
			
			if ($request)
			{
				Write-Output "SUCCESS: Internet Connection Speed $($speed) Mbit/sec" |
				Add-Content -Path $tempfile
			}
		}
		
		Catch
