#Submit request to WHD and Slack.

Function Submit-Request {

    #Validate form filled out
    if ($DetailsBox.TextLength -eq 0){[System.Windows.MessageBox]::Show("Please briefly describe your issue") ; Return}
    if ($NameBox.TextLength -eq 0){[System.Windows.MessageBox]::Show("Please enter a name") ; Return}
    if ($Locationbox.Text -like '' ){[System.Windows.MessageBox]::Show("Please Select a Location") ; Return}
    if ($NumberBox.TextLength -eq 0){[System.Windows.MessageBox]::Show("Please enter a Phone Number") ; Return}
    if ($EmailBox.TextLength -eq 0){[System.Windows.MessageBox]::Show("Please enter an E-mail Address") ; Return}


    #Build request
    $request = [PSCustomObject]@{
        AffectsPatientCare = $PriorityList.Text
        Date = Get-Date -Format G
        Name = $NameBox.Text
        PhoneNumber = $NumberBox.Text
        EmailAddress = $EmailBox.Text
        Location = $LocationBox.Text
        RequestDetails = $DetailsBox.Text
        IPAddress = $ipaddressTextBox.Text
        ComputerName = $Env:ComputerName 
        UserName = $Env:Username 
        TVID = $tvid
        TVLink = "https://start.teamviewer.com/$tvid"
                }
    #Set Priority
    if ($PriorityList.Text -like 'No'){$PriorityNumber = 3}
    if ($PriorityList.Text -like 'Yes'){$PriorityNumber = 1}

    $form.Dispose()

    #Set location ID for whd ticket
    $locationid = $locations | Where-Object Name -Like $request.Location | select -ExpandProperty Number | Out-String
    $locationid = $locationid.trim()

    #Build Detail
    $requestdetail = 'Details: ' + $request.RequestDetails + ' | ' + 'Phone: ' + $request.PhoneNumber + ' | ' + 'TeamViewer ID: ' + $tvid + ' | '  + 'Email Address: ' + $request.EmailAddress + ' | ' + 'ComputerName: ' + $request.ComputerName + ' | ' + 'IP: '  + $request.IPAddress

    #Edit JSON File
    $json = Get-Content $env:Temp\whdJSonFile.json -raw | ConvertFrom-Json
    $json.reportDateUtc = $utcdate
    $json.subject = "IT Support Request"
    $json.detail = $requestdetail
    $json.clientReporter.id = $env:USERNAME
    $json.problemtype.id = '1'
    $json.prioritytype.id = $PriorityNumber
    $json.location.id = $locationid

    #Set JSON File
    $json | ConvertTo-Json -Depth 20 | set-content $env:Temp\whdJSonFile.json

    #Submit ticket
    $response = Invoke-WebRequest "$helpdeskweb/helpdesk/WebObjects/Helpdesk.woa/ra/Tickets?&username=admin&apiKey=$whdapikey" -InFile "$env:Temp\whdJSonFile.json" -Method Post

    #Retrieve Ticket Number
    $ticketnumber = $response.Content | Format-Table -AutoSize | Out-String
    $ticketnumber = ((($ticketnumber -split ":")[1]) -split ",")[0]

    #Add Ticket Number to $request
    $request | Add-Member TicketNumber $ticketnumber

    #Create WHD Link with $ticketnumber to post to slack
    $whdlink = $whdurl + '/helpdesk/WebObjects/Helpdesk.woa/wa/TicketActions/view?ticket=' + $ticketnumber
    
    #Format request for Slack
    $request = $request | Format-List | Out-String

    #Post to slack
    $postSlackMessage = '{"text":' + '"' + "$request" + '"' + '}' 
    Invoke-RestMethod -Method Post -Uri $SlackURL -Body $postSlackMessage
    $postSlackMessage = '{"text":' + '"' + "$whdlink" + '"' + '}' 
    Invoke-RestMethod -Method Post -Uri $SlackURL -Body $postSlackMessage

    #Send confirmation message.
    [System.Windows.MessageBox]::Show("Your Request has been submitted.`n`nYour Ticket Number is: $ticketnumber`n`nDetails:$request")
    

}



$null = $Form.ShowDialog()


}

